mapper.select:
select q.*,t.class_name as class_name,t.name as task_content
from task_queue q inner join task t on t.id = q.task_class
where 1=1

      and q.task_name like "%"#{tq.task_name}"%"


      and t.name like "%"#{tq.name}"%"


      and q.update_time >= #{tq.start_date}


      and q.update_time <= #{tq.end_date}

  order by q.update_time desc

      limit #{tq.start},#{tq.size}

select count(distinct q.id) from task_queue q inner join task t on t.id = q.task_class where 1=1
and q.task_name like "%"#{tq.task_name}"%"
and t.name like "%"#{tq.name}"%"
and q.update_time >= #{tq.start_date}
and q.update_time <= #{tq.end_date}
